import { Component } from '@angular/core';
import {HttpClient} from '@angular/common/http'
@Component({
  selector: 'app-arithmetic',
  templateUrl: './arithmetic.component.html',
  styleUrls: ['./arithmetic.component.css']
})
export class ArithmeticComponent {
num1: number = 0;
num2: number = 0;
operation: string = 'sum';
result: number | null = null;

constructor(private http:HttpClient){}

calculate() {
  const url = `http://localhost:5218/api/arithmetic/${this.operation}?num1=${this.num1}&num2=${this.num2}`;
  this.http.get<number>(url).subscribe(
    (res) => {
      this.result = res;
    },
    (err) => {
      alert('Ocurrió un error: ' + err.error);
    }
  );
}
}
