import { Component } from '@angular/core';
import {HttpClient} from '@angular/common/http'
@Component({
  selector: 'app-arithmetic',
  templateUrl: './arithmetic.component.html',
  styleUrls: ['./arithmetic.component.css']
})
export class ArithmeticComponent {
num1: number = 0;
num2: number = 0;
operation: string = 'sum';
result: number | null = null;

constructor(private http:HttpClient){}

calculate() {
  const url = `http://localhost:45579/api/arithmetic/${this.operation}?num1=${this.num1}&num2=${this.num2}`;
  this.http.get<number>(url).subscribe(
    (res) => {
      this.result = res;
    },
    (err) => {
      alert('Ocurrió un error: ' + err.error);
    }
  );
}
}
<form (ngSubmit)="calculate()">
    <div>
      <label for="num1">Número 1:</label>
      <input id="num1" type="number" [(ngModel)]="num1" name="num1" required />
    </div>
    <div>
      <label for="num2">Número 2:</label>
      <input id="num2" type="number" [(ngModel)]="num2" name="num2" required />
    </div>
    <div>
      <label for="operation">Operación:</label>
      <select id="operation" [(ngModel)]="operation" name="operation">
        <option value="sum">Suma</option>
        <option value="subtract">Resta</option>
        <option value="multiply">Multiplicación</option>
        <option value="divide">División</option>
      </select>
    </div>
    <button type="submit">Calcular</button>
  </form>
  <div *ngIf="result !== null">
    <p>Resultado: {{ result }}</p>
  </div>
